import { StyleSheet } from 'react-native';

const styles = StyleSheet.create({
  btnClickContain: {
    flexDirection: 'row',
    padding: 5,
    marginTop: 5,
    marginBottom: 5,
    alignItems: 'center',
    justifyContent: 'center',
    borderBottomColor: '#e2e2e2',
    borderBottomWidth: 2

  },
  btnContainer: {
    flex: 1,
    flexDirection: 'row',
    alignItems: 'center'
  },
  btnIcon: {
    height: 25,
    width: 25
